import React, { useContext, useEffect, useState } from 'react';
import ContactContext from '../../context/contact/contactContext';

const ContactForm = () => {
    const contactContext = useContext(ContactContext);

    const { addContact, current, clearCurrent, updateContact } = contactContext;

    useEffect(() => {
        if (current !== null) {
            setContact(current);
        } else {
            setContact({
                name: '',
                email: '',
                phone: '',
                type: 'personal'
            });
        }
    }, [contactContext, current]);

    const [contact, setContact] = useState({
        name: '',
        email: '',
        phone: '',
        type: 'personal'
    });

    const { name, email, phone, type } = contact;

    const onChange = e => setContact({ ...contact, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        if (current === null){
            addContact(contact);
        } else {
            updateContact(current);
        }
        clearAll();
    }

    const clearAll = () => {
        clearCurrent();
    }

    return (
        <form onSubmit={onSubmit}>
            <h2 className="text-primary">
                Add Contact
            </h2>
            <input 
                type="text" 
                placeholder='Name' 
                name='name' 
                value={name} 
                onChange={onChange} 
            />
            <input 
                type="email" 
                placeholder='Email' 
                name='email' 
                value={email} 
                onChange={onChange} 
            />
            <input 
                type="text" 
                placeholder='Phone' 
                name='phone' 
                value={phone} 
                onChange={onChange} 
            />
            <h5>Contact Type</h5>
            <input 
                type="radio" 
                name='type' 
                value='personal' 
                onChange={onChange} 
                checked={type === 'personal'} 
            /> Personal{' '}
            <input 
                type="radio" 
                name='type' 
                value='professional' 
                onChange={onChange} 
                checked={type === 'professional'} 
            /> Professional{' '}
            <input 
                type="radio" 
                name='type' 
                value='business' 
                onChange={onChange} 
                checked={type === 'business'} 
            /> Business{' '}
            <input 
                type="radio" 
                name='type' 
                value='family' 
                onChange={onChange} 
                checked={type === 'family'} 
            /> Family
            <div>
                <input type="submit" value='Add Contact' className="btn btn-primary btn-block" />
            </div>
        </form>
    )
}

export default ContactForm;
